/**
 * hook_preprocess_node
 */
function creativ_preprocess_node(&$variables) {
  $node = $variables['node'];
  // Display post information only on certain node types.
  if ($node->type == "article") {
    if (isset($node->field_blog_categories)) {
      $categories = creativ_format_comma_field('field_blog_categories', $node);
      $variables['blog_categories'] = $categories;
    }
    if (isset($node->field_tags)) {
      $tags = creativ_format_comma_field('field_tags', $node);
      $variables['blog_tags'] = $tags;
    } 
  }
}

/**
 * Implement hook_form_search_block_form_alter().
 */
function creativ_form_search_block_form_alter(&$form) {
  $form['search_block_form']['#attributes']['placeholder'] = t('What are you looking for?');
  $form['actions']['#attributes']['class'][] = 'element-invisible';
}

function creativ_format_comma_field($field_category, $node, $limit = NULL) {
  $category_arr = array();
  $field = field_get_items('node', $node, $field_category);
  if (!empty($field)) {
    foreach ($field as $item) {
      $term = taxonomy_term_load($item['tid']);
      if ($term) {
        if (module_exists('i18n_taxonomy')) {
          $term_name = i18n_taxonomy_term_name($term, $node->language);
        } else {
          $term_name = $term->name;
        }
        $category_arr[] = l($term_name, 'taxonomy/term/' . $item['tid']);
      }
      if ($limit) {
        if (count($category_arr) == $limit) {
          $category = implode(', ', $category_arr);
          return $category;
        }
      }
    }
  }
  $category = implode(', ', $category_arr);
  return $category;  
}

/**
 * Improve HTML Validator
 */
function creativ_preprocess_image(&$vars) {
    if (!isset($vars['attributes']['alt'])) {
        $vars['attributes']['alt'] = '';
    }
}
function creativ_preprocess_html(&$vars) {
  //Process portfolio color
  if ($portfolio_category = taxonomy_vocabulary_machine_name_load('portfolio_category')) {
    $terms = taxonomy_get_tree($portfolio_category->vid);
    $less = new lessc();
    $css = '';
    $color = '';
    $class = '';
    foreach ($terms as $t) {
      $term = taxonomy_term_load($t->tid);
      $class = drupal_html_class($t->name);
      if (!empty($term->field_color)) {
        foreach ($term->field_color as $v) {
          $color = $v[0]['value'];
          break;
        }
      }
      if ($color) {
        $css .= ".dexp-masonry-filter,.dexp-portfolio-filter{.{$class} span:before{background-color: {$color} !important;}}";
        $css .= ".{$class} .portfolio-item-overlay{background-color: rgba(red({$color}), green({$color}), blue({$color}), 0.7) !important;}";
      }
    }
    $css = $less->compile($css);
    drupal_add_css($css, array('type' => 'inline'));
  }
  //Process portfolio color
  if ($team_category = taxonomy_vocabulary_machine_name_load('team_category')) {      
    $team_terms = taxonomy_get_tree($team_category->vid);   
    $team_less = new lessc();
    $team_css = '';
    $team_color = '';
    $team_class = '';
    foreach ($team_terms as $t) {
      $team_term = taxonomy_term_load($t->tid);
      $team_class = drupal_html_class($t->name);
      if (!empty($team_term->field_color)) {
        foreach ($team_term->field_color as $v) {
          $team_color = $v[0]['value'];
          break;
        }
      }
      if ($team_color) {
        $team_css .= ".dexp-masonry-filter,.dexp-portfolio-filter{a.{$team_class}{background-color: {$team_color} !important; span{color:#fff !important;}}}";
        $team_css .= ".{$team_class} .portfolio-overlay{background-color: rgba(red({$team_color}), green({$team_color}), blue({$team_color}), 0.7) !important;}";
        //print_r($tesm_css);die();
      }
    }
    $team_css = $team_less->compile($team_css);
    drupal_add_css($team_css, array('type' => 'inline'));
  }
}

